mapper.select:
SELECT attachment_detail_new_name , product_name , product_author , product_price , category_name FROM product p JOIN attachment a ON p.product_no = a.attachment_product_no JOIN attachment_detail d ON a.attachment_no = d.attachment_no JOIN category c ON p.category_no = c.category_no
WHERE (p.product_name LIKE CONCAT('%', #{searchValue}, '%') OR p.product_author LIKE CONCAT('%', #{searchValue}, '%'))
ORDER BY product_created_at DESC LIMIT #{start}, #{perPage};
